import fs from "node:fs";
export default {
    name: "fs-dev",
    load: async (url) => {
        const basePath = "../../assets";
        const body = fs.createReadStream(`${basePath}/${url}`);
        const contentType = url.endsWith(".png") ? "image/png" : "image/jpeg";
        return {
            body,
            contentType,
            cacheControl: "public, max-age=31536000, immutable",
        };
    },
};
